mapper.update:
UPDATE sys_user
id=#{id}, usercode=#{usercode}, username=#{username}, password=#{password}, salt=#{salt}, locked=#{locked}
WHERE id=#{id}

mapper.delete:
        DELETE FROM sys_user	 

		       id = #{id}  
        		       and usercode = #{usercode} 
	    		       and username = #{username} 
	    		       and password = #{password} 
	    		       and salt = #{salt} 
	    		       and locked = #{locked}

mapper.select:
	SELECT
			 id as "id" ,			
			 usercode as "usercode" ,			
			 username as "username" ,			
			 password as "password" ,			
			 salt as "salt" ,			
			 locked as "locked" 			
	FROM  sys_user  

		       id = #{id}  
        		       and usercode = #{usercode} 
	    		       and username = #{username} 
	    		       and password = #{password} 
	    		       and salt = #{salt} 
	    		       and locked = #{locked} 

	order by id desc

			limit #{ startIndex }, #{ maxResults }

		SELECT count(1) 
		FROM sys_user 

		       id = #{id}  
        		       and usercode = #{usercode} 
	    		       and username = #{username} 
	    		       and password = #{password} 
	    		       and salt = #{salt} 
	    		       and locked = #{locked}
